var y = 3; 
function ten() : Integer { 
   println("Called ten");
   10 
} 
def sum = bind ten() + y; 
println(sum); 
y = 7; 
println(sum);

sect2.programlisting:
var min = 0;
var max = 3;
function square(x : Integer) : Integer { x*x }
def values = bind for (x in [min..max]) square(x);  
println(values);
max = 5;  
println(values);
min = 1;  
println(values);
min = 0;  
println(values);

para.programlisting:
bind { 
   def a = expr;  
   def b = expr;  
   def c = expr;  
   expr 
}

sect2.programlisting:
class Point {
  var x : Number;
  var y : Number;
}

var scale = 1.0;
function makePoint(x0 : Number, y0 : Number) : Point {
  Point {
    x: x0 * scale
    y: y0 * scale
  }
}

var myX = 3.0;
var myY = 3.0;
def pt = bind makePoint(myX, myY);
println(pt.x);
myX = 10.0;   
println(pt.x);
scale = 2.0;  
println(pt.x);

para.programlisting:
def pt = bind Point { 
   x: myX
   y: myY  
}

sect2.programlisting:
def pt = bind Point { 
   x: bind myX
   y: myY  
}

def pt = Point { 
   x: bind myX
   y: bind myY  
}

sect1.programlisting:
var note = "do";
var solfege = bind note with inverse;
println( "note={note},  solfege={solfege}" );
note = "re";         
println( "note={note},  solfege={solfege}" );
solfege = "mi";      
println( "note={note},  solfege={solfege}" );
note = "fa";
println( "note={note},  solfege={solfege}" );

class Point {
  var x : Number;
  var y : Number;
  override function toString() : String { "Point({x},{y})" }
}

def pt = Point {
   x: 3.0
   y: 5.0
}
def projectionOnX = Point {
   x: bind pt.x with inverse
   y: 0.0
}
println( pt );
println( projectionOnX );
pt.x = 7.6;    
println( pt );
println( projectionOnX );
projectionOnX.x = 22.2;   
println( pt );
println( projectionOnX );
