sect1.programlisting:
var x : Number;       // x initially 0.0
var m : Integer;      // m initially 0
var isBig : Boolean;  // isBig initially false
var word : String;    // word initially ""
m = 42;
x = 0.25 * m + 2.1e4;
isBig = x > 1000;
word = if (isBig) "Big" else "Small";

class Foo {
  var zing : Integer;
}
var fu : foo;         // fu initially null
fu = Foo {zing: 17}

function gab(val : Number) : Void {
   var remains = val;
   while (remains > 1.0) {
      println( remains );
      remains = remains / 2;
   }
}
